// เรียกใช้ renderlang() เมื่อหน้าเว็บโหลดเสร็จ
$(document).ready(() => {
  renderlang();
});

function setActiveLang() {
  // อ่านค่าจาก Local Storage
  const lang = localStorage.getItem("lang");

  // ตรวจสอบค่าและเพิ่ม class active ให้กับปุ่มที่เลือกไว้
  if (lang === "th") {
    $("#lang-th").addClass("active");
    $("#lang-en").removeClass("active");
  } else if (lang === "en") {
    $("#lang-en").addClass("active");
    $("#lang-th").removeClass("active");
  }
}

// เรียกใช้ function เมื่อหน้าเว็บโหลดเสร็จ
$(document).ready(function () {
  setActiveLang();
});

function settext(key) {
  if (localStorage.lang === "th") {
    return th[key];
  } else if (localStorage.lang === "en") {
    return en[key];
  }
}

$(document).ready(function () {
  $(".lang-a").click(function () {
    $(".lang .active").removeClass("active");
    $(this).addClass("active");
  });
});

$("#lang-th").click(() => {
  localStorage.setItem("lang", "th");
  renderlang();
});

$("#lang-en").click(() => {
  localStorage.setItem("lang", "en");
  renderlang();
});
